import { GeneratorUtils } from './GeneratorUtils';

/** Account activity buckets. */
export class HeightActivityBucketsBuilder {
    /** Account activity buckets. */
    buckets: Uint8Array;

    /**
     * Constructor.
     *
     * @param buckets Account activity buckets.
     */
    public constructor(buckets: Uint8Array) {
        this.buckets = buckets;
    }

    /**
     * Creates an instance of HeightActivityBucketsBuilder from binary payload.
     *
     * @param payload Byte payload to use to serialize the object.
     * @return Instance of HeightActivityBucketsBuilder.
     */
    public static loadFromBinary(payload: Uint8Array): HeightActivityBucketsBuilder {
        const byteArray = Array.from(payload);
        const buckets = GeneratorUtils.getBytes(Uint8Array.from(byteArray), 5);
        byteArray.splice(0, 5);
        return new HeightActivityBucketsBuilder(buckets);
    }

    /**
     * Gets account activity buckets.
     *
     * @return Account activity buckets.
     */
    public getBuckets(): Uint8Array {
        return this.buckets;
    }

    /**
     * Gets the size of the object.
     *
     * @return Size in bytes.
     */
    public getSize(): number {
        let size = 0;
        size += this.buckets.length;
        return size;
    }

    /**
     * Serializes an object to bytes.
     *
     * @return Serialized bytes.
     */
    public serialize(): Uint8Array {
        let newArray = Uint8Array.from([]);
        newArray = GeneratorUtils.concatTypedArrays(newArray, this.buckets);
        return newArray;
    }
}
